ecution_count":null,"outputs":[]},{"cell_type":"code","source":["\n"],"metadata":{"id":"sxqLqHi4sLJw"},"execution_count":null,"outputs":[]},{"cell_type":"code","source":["#Replacing \"/\", \"@\" and \"|\" with space\n","toSpace <- content_transformer(function (x , pattern ) gsub(pattern, \" \", x))\n","TextDoc <- tm_map(TextDoc, toSpace, \"/\")\n","TextDoc <- tm_map(TextDoc, toSpace, \"@\")\n","TextDoc <- tm_map(TextDoc, toSpace, \"\\\\|\")\n","# Convert the text to lower case\n","TextDoc <- tm_map(TextDoc, content_transformer(tolower))\n","# Remove numbers\n","TextDoc <- tm_map(TextDoc, removeNumbers)\n","# Remove english common stopwords\n","TextDoc <- tm_map(TextDoc, removeWords, stopwords(\"english\"))\n","# Remove your own stop word\n","# specify your custom stopwords as a character vector\n","TextDoc <- tm_map(TextDoc, removeWords, c(\"s\", \"company\", \"team\"))\n","# Remove punctuations\n","TextDoc <- tm_map(TextDoc, removePunctuation)\n","# Eliminate extra white spaces\n","TextDoc <- tm_map(TextDoc, stripWhitespace)\n","# Text stemming - which reduces words to their root form\n","TextDoc <- tm_map(TextDoc, stemDocument)\n"],"metadata":{"colab":{"base_uri":"https://localhost:8080/","height":106},"id":"yZDM4lDes6qQ","executionInfo":{"status":"error","timestamp":1721639354648,"user_tz":-330,"elapsed":437,"user":{"displayName":"Bhavani Rajpurohit","userId":"04861995456098549452"}},"outputId":"c8d91a0c-df58-4a05-ed35-908b3e10f670"},"execution_count":null,"outputs":[{"output_type":"error","ename":"ERROR","evalue":"Error in eval(expr, envir, enclos): object 'TextDoc' not found\n","traceback":["Error in eval(expr, envir, enclos): object 'TextDoc' not found\nTraceback:\n","1. tm_map(TextDoc, toSpace, \"/\")"]}]}]}